def to_realvec(matrix):
    rows = len(matrix)
    cols = len(matrix[0])
    vec = realvec(rows, cols)
    for i in range(0, rows):
        for j in range(0, cols):
            vec[i*cols+j] = matrix[i][j]
    return vec

def to_array(vec):
    rows = vec.getRows()
    cols = vec.getCols()
    arr = zeros((rows, cols))
    for i in range(0, rows):
        for j in range(0, cols):
            arr[i][j] = vec[i*cols + j]
    return arr

def transpose(vec):
    rows = vec.getRows()
    cols = vec.getCols()
    v1 = to_array(vec)
    v2 = zeros((cols,rows))
    for i in range(0, rows):
        for j in range(0, cols):
            v2[j][i] = v1[i][j]
    return to_realvec(v2)


